package week1.day1;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// datatype varaiableName = value //
		//Integer 
		//byte
		byte ageOf=5;
		System.out.println(ageOf);

		
		//short
		short ageOfe=35;
		System.out.println(ageOfe);

		
		//int  
		int saranyaDaku=3000;
		System.out.println(saranyaDaku);

		
		//long -> use this letter with L orl
		
		long mobileNumber=976787567l;
		
		System.out.println(mobileNumber);

		//float->onluable to 6,7 decimal
		
		float rateOfInterest=7.35F;
		System.out.println(rateOfInterest);

		
		//double -> use more accurate value
		//it will accept more than float 
		
		double rate2=2.3457645;
		float rate3=2.45466f;
	System.out.println(rate3);
	System.out.println(rate2);
	
	// Char -> it can store only one charcter
	//it may alphates, num, special character
	//always it will come with single quotes
	
	char intial='m';
	System.out.println(intial);
	char special='$';
			System.out.println(special);
	//boolean
			//true or false statement
			
			boolean doYouKnow=true;
//String - collections of charcters
			String name="daksha";
			System.out.println(name);

			
			
		
	}

}
